        //     )) &&
        //   (!firstSpot ||
        //     !thirdSpot ||
        //     !equalLines(
        //       line,
        //       findLine(triangle.x1, triangle.y1, triangle.x3, triangle.y3)[0]
        //     ));
        // setLines(lines.filter((line) => condition(line)));
        setLines([]);
      }
      return returnVal;
    }
    return true;
  }

  function isConfirmed(triangle: Triangle) {
    return (
      confirmedTriangles.filter(
        (tri) =>
          tri.x1 === triangle.x1 &&
          tri.y1 === triangle.y1 &&
          tri.x2 === triangle.x2 &&
          tri.y2 === triangle.y2 &&
          tri.x3 === triangle.x3 &&
          tri.y3 === triangle.y3 &&
          tri.fill === triangle.fill
      ).length > 0
    );
  }

  function equalLines(line1: number[], line2: number[]) {
    return (
      line1[0] == line2[0] &&
      line1[1] == line2[1] &&
      line1[2] == line2[2] &&
      line1[3] == line2[3]
    );
  }

  function findLine(x1: number, y1: number, x2: number, y2: number) {
    return lines.filter(
      (coords) =>
        (coords[0] == x1 &&
          coords[1] == y1 &&
          coords[2] == x2 &&
          coords[3] == y2) ||
        (coords[0] == x2 &&
          coords[1] == y2 &&
          coords[2] == x1 &&
          coords[3] == y1)
    );
  }

  function undoLastLine() {
    let lastLines = lines;
    lastLines.pop();
    setLines(lastLines);
  }

  function numberOfCircles(spot: number[]) {
    let count = 0;
    triangles.map((triangle) => {
      if (!isConfirmed(triangle)) {
        if (triangle.x1 == spot[0] && triangle.y1 == spot[1]) {
          count += 1;
        }
        if (triangle.x2 == spot[0] && triangle.y2 == spot[1]) {
          count += 1;
        }
        if (triangle.x3 == spot[0] && triangle.y3 == spot[1]) {
          count += 1;
        }
      }
    });

    return triangles.filter(
      (triangle) =>
        !isConfirmed(triangle) &&
        ((triangle.x1 == spot[0] && triangle.y1 == spot[1]) ||
          (triangle.x2 == spot[0] && triangle.y2 == spot[1]) ||
          (triangle.x3 == spot[0] && triangle.y3 == spot[1]))
    ).length;
