      document.removeEventListener("mousedown", handleMouseDown);
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [startCoords, endCoords]);

  useEffect(() => {
    triangles.filter((triangle) => isConfirmed(triangle)).length ==
      triangles.length;
  }, [confirmedTriangles]);

  return (
    <>
      <div className="home" onClick={() => setSection(SECTION.NONE)}>
        {HomeIcon}
      </div>
      <div
        className="vertex-board"
        style={{ width: "100%", height: window.innerHeight }}
      >
        <div className="spots">
          {triangles.map(
            (triangle) =>
              allLines(triangle) && (
                <svg className="strands-svg" key={triangle.toString()}>
                  {
                    <polygon
                      points={
                        triangle.x1.toString() +
                        "," +
                        triangle.y1.toString() +
                        " " +
                        triangle.x2.toString() +
                        "," +
                        triangle.y2.toString() +
                        " " +
                        triangle.x3.toString() +
                        "," +
                        triangle.y3.toString()
                      }
                      style={{
                        fill: triangle.fill,
                        stroke: "black",
                        strokeWidth: "0",
                      }}
                    />
                  }
                </svg>
              )
          )}
          {spots.map(
            (spot) =>
              numberOfCircles(spot) > 0 && (
                <svg
                  className="vertex-svg"
                  key={spot.toString() + Math.random() * 100}
                  style={{ zIndex: 5 }}
                >
                  <circle
                    r={numberOfCircles(spot) + 5}
                    cx={spot[0]}
                    cy={spot[1]}
                    fill="white"
                    stroke="black"
                    strokeWidth="0.5"
                  />
                  <text
                    x={spot[0]}
                    y={spot[1]}
                    textAnchor="middle"
                    alignmentBaseline="middle"
                    fontSize={numberOfCircles(spot) + 3}
                    fill="black"
                  >
                    {numberOfCircles(spot)}
                  </text>
                </svg>
              )
          )}
          {startCoords.length == 2 && endCoords.length == 2 && (
            <svg
              className="vertex-lines"
              key={
                startCoords.toString() +
                endCoords.toString() +
                Math.random() * 100
              }
              style={{ zIndex: -5 }}
            >
              <line
                x1={startCoords[0]}
                y1={startCoords[1]}
                x2={endCoords[0]}
                y2={endCoords[1]}
                stroke="black"
                strokeWidth="1"
              />
            </svg>
          )}
          {lines.map((line) => (
            <svg
              className="vertex-lines"
              key={
                startCoords.toString() +
                endCoords.toString() +
                Math.random() * 100
              }
              style={{ zIndex: -5 }}
            >
              <line
                x1={line[0]}
                y1={line[1]}
                x2={line[2]}
                y2={line[3]}
                stroke="black"
                strokeWidth="1"
              />
            </svg>
          ))}
          <button
            onClick={undoLastLine}
            style={{ position: "fixed", bottom: 10 }}
          >
            Undo
          </button>
        </div>
      </div>
    </>
  );
}
