import { MenuProps, SECTION } from "../App";
import { HomeIcon } from "../icons/HomeIcon";
import "../styles/strands.css";

export default function Strands({ section, setSection }: MenuProps) {
  const initial = [
    ["D", "U", "P", "L", "O", "M", "I", "M"],
    ["S", "I", "I", "E", "V", "K", "A", "S"],
    ["S", "I", "N", "T", "N", "O", "C", "T"],
    ["E", "N", "C", "E", "P", "L", "A", "C"],
    ["G", "O", "O", "D", "C", "E", "P", "A"],
    ["S", "T", "L", "I", "V", "E", "S", "T"],
    ["H", "A", "L", "L", "E", "N", "G", "E"],
    ["R", "S", "A", "N", "A", "T", "O", "M"],
    ["Y", "O", "F", "A", "F", "A", "L", "L"],
  ];

  const correct = [
    "DIPLOMAT",
    "SUITS",
    "KIMSCONVENIENCE",
    "GOODPLACE",
    "PASTLIVES",
    "CHALLENGERS",
    "ANATOMYOFAFALL",
  ];

  let sum = 9;
  correct.map((item) => (sum += item.length));
  console.log(sum);

  const pangram = "WATCHLIST";

  function selectLetter(el: any) {
    if (el.classList.contains("selected")) {
      el.classList.remove("selected");
    } else {
      el.classList.add("selected");
    }
  }

  return (
    <>
      <div className="home" onClick={() => setSection(SECTION.NONE)}>
        {HomeIcon}
      </div>
      <div className="body" style={{ height: window.innerHeight }}>
        <input readOnly />
        <div className="strands-board">
          {initial.map((row) => (
            <div className="strands-row">
              {row.map((letter) => (
                <p
                  className="letter"
                  draggable="true"
                  onClick={(e) => selectLetter(e.target)}
                >
                  {letter}
                </p>
              ))}
            </div>
          ))}
        </div>
        <div className="footer"></div>
      </div>
    </>
  );
}
