import { useEffect } from "react";
import "../styles/board.css";

export default function Board() {
  const correct = {
    "Date nights": ["Den Den", "Haruki", "Aleppo Sweets", "East End"],
    "Three track mind": ["Poker", "Golf", "Abstract", "Food"],
    "Terms of endearment we use": ["Pookie", "Baby", "Cutie Pie", "Love"],
    "Stuff we've done together": ["Legos", "Sled", "Study", "Pack"],
  };

  const shuffle = (array: string[]) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const items = Object.values(correct).flat();

  function selectItem(el: any) {
    if (document.querySelectorAll(".selected").length < 4) {
      if (el.classList.contains("selected")) {
        el.classList.remove("selected");
      } else {
        el.classList.add("selected");
      }
    } else {
      if (el.classList.contains("selected")) {
        el.classList.remove("selected");
      }
    }
  }

  function checkItems() {
    let submitted: string[] = [];
    document
      .querySelectorAll(".selected")
      .forEach((item) => submitted.push(item.textContent || ""));
    if (submitted.length == 4) {
      Object.values(correct).map(
        (value) =>
          value.sort().filter((item, index) => item == submitted.sort()[index])
            .length == 4 && alert("Yay!")
      );
    }
    console.log(submitted);
  }

  return (
    <div className="body" style={{ height: window.innerHeight }}>
      <div className="board">
        {items.map(
          (item, index) =>
            index < 16 && (
              <p className="item" onClick={(e) => selectItem(e.target)}>
                {item}
              </p>
            )
        )}
      </div>
      <div className="footer">
        <button className="submit" onClick={checkItems}>
          Submit
        </button>
      </div>
    </div>
  );
}
