import { useState } from "react";
import { MenuProps, SECTION } from "../App";
import { HomeIcon } from "../icons/HomeIcon";
import "../styles/strands.css";

export default function Strands({ section, setSection }: MenuProps) {
  const [selectedWord, setSelectedWord] = useState<string>("");
  const initial = [
    ["D", "I", "P", "L", "O", "K", "I", "T"],
    ["S", "U", "I", "T", "S", "M", "A", "M"],
    ["I", "N", "E", "V", "N", "O", "C", "S"],
    ["E", "N", "C", "E", "G", "O", "O", "D"],
    ["W", "A", "T", "C", "H", "L", "I", "P"],
    ["T", "L", "I", "V", "E", "T", "S", "L"],
    ["S", "L", "L", "E", "S", "E", "R", "A"],
    ["A", "A", "O", "N", "G", "F", "S", "C"],
    ["P", "T", "H", "M", "Y", "A", "A", "E"],
    ["A", "N", "A", "C", "O", "F", "L", "L"],
  ];

  const [dragging, setDragging] = useState<boolean>(false);
  const [draggedLetters, setDraggedLetters] = useState<string[]>([]);

  function handleMouseDown(
    e: React.MouseEvent<HTMLParagraphElement, MouseEvent>
  ) {
    e.preventDefault();
    setDragging(true);
    setSelectedWord("");
    setDraggedLetters([e.currentTarget.id]);
    e.currentTarget.classList.add("strands-selected");
  }

  function handleMouseOver(
    e: React.MouseEvent<HTMLParagraphElement, MouseEvent>
  ) {
    if (dragging) {
      const newLetter = e.currentTarget.id;
      const newDraggedLetters = [...draggedLetters, newLetter];
      setDraggedLetters(newDraggedLetters);
      e.currentTarget.classList.add("strands-selected");
    }
  }

  function handleMouseUp() {
    setDragging(false);
    setSelectedWord(
      draggedLetters
        .map((id) => document.getElementById(id)?.innerText)
        .join("")
    );
    // Clear dragged letters
    setDraggedLetters([]);
  }

  const correct = [
    "DIPLOMAT",
    "SUITS",
    "KIMSCONVENIENCE",
    "GOODPLACE",
    "PASTLIVES",
    "CHALLENGERS",
    "ANATOMYOFAFALL",
  ];

  function selectLetter(el: any) {
    if (el.classList.contains("strands-selected")) {
      el.classList.remove("strands-selected");
    } else {
      el.classList.add("strands-selected");
    }

    const selectedElems = document.querySelectorAll(".strands-selected");
    setSelectedWord(selectedWord + el.innerHTML);
    if (
      correct.filter((word) => word == selectedWord + el.innerHTML).length == 1
    ) {
      selectedElems.forEach(
        (e) =>
          (document.getElementById(e.id)!.style.backgroundColor = "#aedfee")
      );
      selectedElems.forEach((e) => e.classList.remove("strands-selected"));
      setSelectedWord("");
    } else if (selectedWord + el.innerHTML == pangram) {
      selectedElems.forEach(
        (e) =>
          (document.getElementById(e.id)!.style.backgroundColor = "#f8cb2c")
      );
      selectedElems.forEach((e) => e.classList.remove("strands-selected"));
      setSelectedWord("");
    }
  }

  const pangram = "WATCHLIST";

  return (
    <>
      <div className="home" onClick={() => setSection(SECTION.NONE)}>
        {HomeIcon}
      </div>
      <div className="body" style={{ height: window.innerHeight }}>
        <input readOnly value={selectedWord} />
        <div className="strands-board">
          {initial.map((row, rowIndex) => (
            <div className="strands-row">
              {row.map((letter, colIndex) => (
                <p
                  className="letter"
                  id={letter + Math.floor(Math.random() * 10000).toString()}
                  onClick={(e) => selectLetter(e.target)}
                >
                  {letter}
                </p>
              ))}
            </div>
          ))}
        </div>
        <div className="footer"></div>
      </div>
    </>
  );
}
