import { useEffect, useState } from "react";
import "../styles/board.css";

type Row = {
  category: string;
  level: number;
  items: string[];
};

export default function Board() {
  let correct: Row[] = [
    {
      category: "Date nights",
      level: 1,
      items: ["Den Den", "Haruki", "Aleppo Sweets", "East End"],
    },
    {
      category: "Three track mind",
      level: 3,
      items: ["Poker", "Golf", "Abstract", "Food"],
    },
    {
      category: "Terms of endearment we use",
      level: 2,
      items: ["Pookie", "Baby", "Cutie Pie", "Love"],
    },
    {
      category: "Stuff we've done together",
      level: 4,
      items: ["Legos", "Sled", "Study", "Pack"],
    },
  ];

  let items = [
    "Love",
    "Food",
    "Study",
    "Poker",
    "Den Den",
    "Pookie",
    "Baby",
    "Abstract",
    "Haruki",
    "Aleppo Sweets",
    "Legos",
    "East End",
    "Pack",
    "Golf",
    "Sled",
    "Cutie Pie",
  ];

  const [rows, setRows] = useState<Row[]>([]);

  const backgrounds = ["lightblue", "lightgreen", "pink", "rgb(186, 129, 197)"];

  function selectItem(el: any) {
    if (el.tagName == "P") {
      el = el.parentNode;
    }
    if (document.querySelectorAll(".selected").length < 4) {
      if (el.classList.contains("selected")) {
        el.classList.remove("selected");
      } else {
        el.classList.add("selected");
      }
    } else {
      if (el.classList.contains("selected")) {
        el.classList.remove("selected");
      }
    }

    if (document.querySelectorAll(".selected").length == 4) {
      document.getElementById("submit")!.style.backgroundColor = "rgb(0, 0, 0)";
      document.getElementById("submit")!.style.color = "rgb(255, 255, 255)";
      document.getElementById("submit")!.style.border = "none";
    } else {
      document.getElementById("submit")!.style.backgroundColor =
        "rgb(255, 255, 255)";
      document.getElementById("submit")!.style.color = "rgb(127, 127, 127)";
      document.getElementById("submit")!.style.borderColor =
        "rgb(127, 127, 127)";
      document.getElementById("submit")!.style.borderStyle = "solid";
    }
  }

  function correctItems(value: Row) {
    document.querySelectorAll(".selected").forEach((item) => {
      item.classList.remove("selected");
    });
    value.items.map(
      (item) => (document.getElementById(item)!.style.display = "none")
    );

    setRows([...rows, value]);
  }

  function checkItems() {
    let submitted: string[] = [];
    document
      .querySelectorAll(".selected")
      .forEach((item) => submitted.push(item.textContent || ""));
    if (submitted.length == 4) {
      correct.map(
        (row) =>
          row.items
            .sort()
            .filter((item, index) => item == submitted.sort()[index]).length ==
            4 && correctItems(row)
      );
    }
  }

  return (
    <div className="body" style={{ height: window.innerHeight }}>
      <div className="board">
        {rows.map((item) => (
          <div
            className="row"
            style={{ backgroundColor: backgrounds[item.level - 1] }}
          >
            <p className="row-category">{item.category}</p>
            <p className="row-items">
              {item.items.toString().replaceAll(",", ", ")}
            </p>
          </div>
        ))}
        {items.map((item) => (
          <div className="item" onClick={(e) => selectItem(e.target)} id={item}>
            <p>{item}</p>
          </div>
        ))}
      </div>
      <div className="footer">
        {rows.length != 4 && (
          <button className="submit" id="submit" onClick={checkItems}>
            Submit
          </button>
        )}
      </div>
    </div>
  );
}
