import { Dispatch, SetStateAction, useState } from "react";
import Menu from "./components/Menu";
import Connections, { Row } from "./components/Connections";
import Strands from "./components/Strands";
import SpellingBee from "./components/Vertex";
import Vertex from "./components/Vertex";

export type MenuProps = {
  section: SECTION;
  setSection: Dispatch<SetStateAction<SECTION>>;
};

export enum SECTION {
  NONE,
  CONNECTIONS,
  STRANDS,
  SPELLINGBEE,
  VERTEX,
}

function App() {
  const [section, setSection] = useState<SECTION>(SECTION.NONE);
  const [rows, setRows] = useState<Row[]>([]);
  const [noShow, setNoShow] = useState<string[]>([]);
  const [finalLines, setFinalLines] = useState<any[]>([]);
  const [pangramLines, setPangramLines] = useState<any[]>([]);
  const [wordCount, setWordCount] = useState<number>(0);
  const [foundWords, setFoundWords] = useState<string[]>([]);
  const [foundPangram, setFoundPangram] = useState<string[]>([]);
  return (
    <div className="App" style={{ width: window.innerWidth }}>
      {section == SECTION.NONE && (
        <Menu section={section} setSection={setSection} />
      )}
      {section == SECTION.CONNECTIONS && (
        <Connections
          section={section}
          setSection={setSection}
          rows={rows}
          setRows={setRows}
          noShow={noShow}
          setNoShow={setNoShow}
        />
      )}
      {section == SECTION.STRANDS && (
        <Strands
          section={section}
          setSection={setSection}
          finalLines={finalLines}
          setFinalLines={setFinalLines}
          pangramLines={pangramLines}
          setPangramLines={setPangramLines}
          wordCount={wordCount}
          setWordCount={setWordCount}
          foundWords={foundWords}
          setFoundWords={setFoundWords}
          foundPangram={foundPangram}
          setFoundPangram={setFoundPangram}
        />
      )}
      {section == SECTION.VERTEX && (
        <Vertex section={section} setSection={setSection} />
      )}
    </div>
  );
}

export default App;
