import { MenuProps, SECTION } from "../App";
import { HomeIcon } from "../icons/HomeIcon";
import "../styles/strands.css";

export default function Strands({ section, setSection }: MenuProps) {
  const initial = [
    ["D", "I", "P", "L", "O", "M", "A", "T"],
    ["S", "U", "I", "T", "S", "K", "I", "M"],
    ["S", "C", "O", "N", "V", "E", "N", "I"],
    ["E", "N", "C", "E", "G", "O", "O", "D"],
    ["P", "L", "A", "C", "E", "P", "A", "S"],
    ["T", "L", "I", "V", "E", "S", "C", "H"],
    ["A", "L", "L", "E", "N", "G", "E", "R"],
    ["S", "A", "N", "A", "T", "O", "M", "Y"],
    ["O", "F", "A", "F", "A", "L", "L", "W"],
    ["A", "T", "C", "H", "L", "I", "S", "T"],
  ];

  const correct = [
    "DIPLOMAT",
    "SUITS",
    "KIMSCONVENIENCE",
    "GOODPLACE",
    "PASTLIVES",
    "CHALLENGERS",
    "ANATOMYOFAFALL",
  ];

  function selectLetter(el: any) {
    let backgrounds = [
      "blue",
      "purple",
      "green",
      "pink",
      "orange",
      "yellow",
      "gray",
    ];
    if (el.classList.contains("selected")) {
      el.classList.remove("selected");
    } else {
      el.classList.add("selected");
    }

    const selectedElems = document.querySelectorAll(".selected");
    /*if (selectedElems.length == 2) {
      const firstVal = selectedElems[0].innerHTML;
      selectedElems[0].innerHTML = selectedElems[1].innerHTML;
      selectedElems[1].innerHTML = firstVal;
      selectedElems.forEach((el) => el.classList.remove("selected"));
    }*/
    let selectedWord = "";
    selectedElems.forEach((el) => (selectedWord += el.innerHTML));
    console.log(selectedWord);
    if (
      correct.filter((word) => word.length == selectedWord.length).length == 1
    ) {
      const background = backgrounds.pop() || "white";
      selectedElems.forEach(
        (el) =>
          (document.getElementById(el.innerHTML)!.style.backgroundColor =
            background)
      );
      selectedElems.forEach((el) => el.classList.remove("selected"));
    }
  }

  const pangram = "WATCHLIST";

  return (
    <>
      <div className="home" onClick={() => setSection(SECTION.NONE)}>
        {HomeIcon}
      </div>
      <div className="body" style={{ height: window.innerHeight }}>
        <input readOnly />
        <div className="strands-board">
          {initial.map((row) => (
            <div className="strands-row">
              {row.map((letter) => (
                <p
                  className="letter"
                  id={letter}
                  onClick={(e) => selectLetter(e.target)}
                >
                  {letter}
                </p>
              ))}
            </div>
          ))}
        </div>
        <div className="footer"></div>
      </div>
    </>
  );
}
