import { useEffect } from "react";
import "../styles/board.css";

export default function Board() {
  const correct = {
    "Shows we've watched": [
      "Diplomat",
      "Suits",
      "Kim's Convenience",
      "Good Place",
    ],
    "Movies we've watched": [
      "Past Lives",
      "Anatomy of a Fall",
      "Mr and Mrs Smith",
      "Challengers",
    ],
    "Date nights": ["Den Den", "Haruki", "Aleppo Sweets", "East End"],
    "Three track mind": ["Poker", "Golf", "Abstract", "Food"],
    "Terms of endearment we use": ["Pookie", "Baby", "Cutie Pie", "Love"],
    "Stuff we've done together": ["Legos", "Sled", "Study", "Pack"],
    "Songs on Kims": ["Sativa", "Risk", "Girls Need Love", "Some"],
    "CS classes we've taken together": ["19", "30", "32", "1470"],
    "Numbers for our birthdays": ["05", "31", "08", "07"],
  };

  const shuffle = (array: string[]) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const items = shuffle(Object.values(correct).flat());

  useEffect(() => {}, []);

  return (
    <>
      <div className="board">
        {items.map(
          (item, index) => index < 16 && <p className="item">{item}</p>
        )}
      </div>
    </>
  );
}
